CREATE SCHEMA app;

CREATE TABLE app.digimon (
  name TEXT NOT NULL,
  CONSTRAINT digimon_pk PRIMARY KEY (name)
);


CREATE TABLE app.evo_requirements (
  name TEXT NOT NULL,
  evolves_from TEXT NOT NULL,
  hp TEXT NOT NULL,
  mp TEXT NOT NULL,
  atk TEXT NOT NULL,
  def TEXT NOT NULL,
  spd TEXT NOT NULL,
  int TEXT NOT NULL,
  weight TEXT NOT NULL,
  mistake TEXT NOT NULL,
  happiness TEXT NOT NULL,
  discipline TEXT NOT NULL,
  battles TEXT NOT NULL,
  techs TEXT NOT NULL,
  decode TEXT NOT NULL,
  quota TEXT NOT NULL,
  CONSTRAINT evo_req_pk PRIMARY KEY (name, evolves_from)
);

